package fr.jaxio.demo.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import fr.jaxio.demo.dao.support.SearchParameters;
import fr.jaxio.demo.domain.Account;
import fr.jaxio.demo.service.AccountService;
import fr.jaxio.demo.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 * Note: instanciate it as a var from your flow to avoid http://jira.springframework.org/browse/SWF-1224
 */
public class AccountLazyDataModel extends GenericLazyDataModel<Account> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private AccountService accountService;
    @Inject
    transient private AccountSearchForm accountSearchForm;

    /**
     * Prepare the search parameters and call the accountService finder.
     * Automatically called by PrimeFaces component.
     */
    @Override
    public List<Account> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = accountSearchForm.getSearchParameters();
        sp.clearRanges();
        sp.addRange(accountSearchForm.getBirthDateRange());

        Account account = accountSearchForm.getAccount();

        // total count so the paginator may display the total number of pages
        setRowCount(accountService.findCount(account, sp));

        // load one page of data
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters);
        return accountService.find(account, sp);
    }
}