package fr.jaxio.demo.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import fr.jaxio.demo.dao.support.SearchParameters;
import fr.jaxio.demo.domain.Book;
import fr.jaxio.demo.service.BookService;
import fr.jaxio.demo.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 * Note: instanciate it as a var from your flow to avoid http://jira.springframework.org/browse/SWF-1224
 */
public class BookLazyDataModel extends GenericLazyDataModel<Book> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private BookService bookService;
    @Inject
    transient private BookSearchForm bookSearchForm;

    /**
     * Prepare the search parameters and call the bookService finder.
     * Automatically called by PrimeFaces component.
     */
    @Override
    public List<Book> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = bookSearchForm.getSearchParameters();
        sp.clearRanges();
        sp.addRange(bookSearchForm.getNumberOfPagesRange());

        Book book = bookSearchForm.getBook();

        // total count so the paginator may display the total number of pages
        setRowCount(bookService.findCount(book, sp));

        // load one page of data
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters);
        return bookService.find(book, sp);
    }
}