package fr.jaxio.demo.web.selenium.page.address;

import static org.openqa.selenium.By.cssSelector;

import org.openqa.selenium.WebElement;
import org.openqa.selenium.support.FindBy;

import fr.jaxio.demo.web.selenium.support.Page;
import fr.jaxio.demo.web.selenium.page.AbstractSearchPage;

@Page
public class AddressSearchPage extends AbstractSearchPage {
    // search box
    @FindBy(id = "form:streetName")
    public WebElement streetName;
    @FindBy(id = "form:city")
    public WebElement city;

    public void clickEditAddress(String address) {
        client.click(cssSelector("button[title=\"Edit " + address + "\"]"));
    }

    public void clickAddAddress(String address) {
        client.click(cssSelector("button[title=\"Add " + address + "\"]"));
    }

    public void clickDeleteAddress(String address) {
        client.click(cssSelector("button[title=\"Delete " + address + "\"]"));
    }

    public void clickSelectAddress(String address) {
        client.click(cssSelector("button[title=\"Select " + address + "\"]"));
    }
}