package fr.jaxio.demo.web.selenium.page.book;

import static org.openqa.selenium.By.cssSelector;

import org.openqa.selenium.WebElement;
import org.openqa.selenium.support.FindBy;

import fr.jaxio.demo.web.selenium.support.Page;
import fr.jaxio.demo.web.selenium.page.AbstractSearchPage;

@Page
public class BookSearchPage extends AbstractSearchPage {
    // search box
    @FindBy(id = "form:account")
    public WebElement account;
    @FindBy(id = "form:title")
    public WebElement title;
    @FindBy(id = "form:numberOfPages")
    public WebElement numberOfPages;

    public void clickEditBook(String book) {
        client.click(cssSelector("button[title=\"Edit " + book + "\"]"));
    }

    public void clickAddBook(String book) {
        client.click(cssSelector("button[title=\"Add " + book + "\"]"));
    }

    public void clickDeleteBook(String book) {
        client.click(cssSelector("button[title=\"Delete " + book + "\"]"));
    }

    public void clickSelectBook(String book) {
        client.click(cssSelector("button[title=\"Select " + book + "\"]"));
    }
}