package com.jaxio.demo.formatter.support;

import java.util.Locale;

import org.springframework.data.domain.Persistable;
import org.springframework.expression.Expression;
import org.springframework.expression.ExpressionParser;
import org.springframework.expression.spel.standard.SpelExpressionParser;

public abstract class FormatterUtil {

    static public <E extends Persistable<?>> String print(String printExpression, E entity, Locale locale) {
        if (entity == null) {
            return "";
        }

        ExpressionParser parser = new SpelExpressionParser();
        Expression exp = parser.parseExpression(printExpression);
        return exp.getValue(entity, String.class);
    }
}
