package com.jaxio.web.domain;

import static com.jaxio.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.jaxio.domain.Account;
import com.jaxio.repository.AccountRepository;
import com.jaxio.web.converter.domain.AccountConverter;
import com.jaxio.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class AccountController {
    private static final long serialVersionUID = 1L;

    @Inject
    private AccountRepository accountRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private AccountConverter accountConverter;

    public Account newIfNull(Account account) {
        if (account != null) {
            return account;
        }

        return accountRepository.getNewWithDefaults();
    }

    public void save(Account account) {
        accountRepository.save(account);
        messageUtil.info("status_saved_ok", accountConverter.print(account));
    }

    public void saveAndClose(Account account) {
        accountRepository.save(account);
        messageUtil.infoDelayed("status_saved_ok", accountConverter.print(account));
        forceClose();
    }

    public void delete(Account account) {
        String infoArg = accountConverter.print(account);
        accountRepository.delete(account);
        messageUtil.info("status_deleted_ok", infoArg);
    }
}