package com.jaxio.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import com.jaxio.dao.support.SearchParameters;
import com.jaxio.domain.Address;
import com.jaxio.repository.AddressRepository;
import com.jaxio.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 *
 * @see http://jira.springframework.org/browse/SWF-1224 to avoid instanciate it as a var from your flow 
 */
public class AddressLazyDataModel extends GenericLazyDataModel<Address> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private AddressRepository addressRepository;
    @Inject
    transient private AddressSearchForm addressSearchForm;

    /**
     * Prepare the search parameters and call the addressRepository finder.
     * Automatically called by PrimeFaces component.
     */
    @Override
    public List<Address> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = addressSearchForm.getSearchParameters();

        Address address = addressSearchForm.getAddress();
        setRowCount(addressRepository.findCount(address, sp)); // total count so the paginator may display the total number of pages
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters); // load one page of data

        return addressRepository.find(address, sp);
    }
}