package com.jaxio.web.domain;

import static com.jaxio.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.jaxio.domain.Book;
import com.jaxio.repository.BookRepository;
import com.jaxio.web.converter.domain.BookConverter;
import com.jaxio.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class BookController {
    private static final long serialVersionUID = 1L;

    @Inject
    private BookRepository bookRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private BookConverter bookConverter;

    public Book newIfNull(Book book) {
        if (book != null) {
            return book;
        }

        return bookRepository.getNewWithDefaults();
    }

    public void save(Book book) {
        bookRepository.save(book);
        messageUtil.info("status_saved_ok", bookConverter.print(book));
    }

    public void saveAndClose(Book book) {
        bookRepository.save(book);
        messageUtil.infoDelayed("status_saved_ok", bookConverter.print(book));
        forceClose();
    }

    public void delete(Book book) {
        String infoArg = bookConverter.print(book);
        bookRepository.delete(book);
        messageUtil.info("status_deleted_ok", infoArg);
    }
}