package com.jaxio.web.domain;

import static com.jaxio.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.jaxio.domain.Document;
import com.jaxio.repository.DocumentRepository;
import com.jaxio.web.converter.domain.DocumentConverter;
import com.jaxio.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class DocumentController {
    private static final long serialVersionUID = 1L;

    @Inject
    private DocumentRepository documentRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private DocumentConverter documentConverter;

    public Document newIfNull(Document document) {
        if (document != null) {
            return document;
        }

        return documentRepository.getNewWithDefaults();
    }

    public void save(Document document) {
        documentRepository.save(document);
        messageUtil.info("status_saved_ok", documentConverter.print(document));
    }

    public void saveAndClose(Document document) {
        documentRepository.save(document);
        messageUtil.infoDelayed("status_saved_ok", documentConverter.print(document));
        forceClose();
    }

    public void delete(Document document) {
        String infoArg = documentConverter.print(document);
        documentRepository.delete(document);
        messageUtil.info("status_deleted_ok", infoArg);
    }
}