package com.jaxio.web.domain;

import static com.jaxio.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.jaxio.domain.Legacy;
import com.jaxio.repository.LegacyRepository;
import com.jaxio.web.converter.domain.LegacyConverter;
import com.jaxio.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class LegacyController {
    private static final long serialVersionUID = 1L;

    @Inject
    private LegacyRepository legacyRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private LegacyConverter legacyConverter;

    public Legacy newIfNull(Legacy legacy) {
        if (legacy != null) {
            return legacy;
        }

        return legacyRepository.getNewWithDefaults();
    }

    public void save(Legacy legacy) {
        legacyRepository.save(legacy);
        messageUtil.info("status_saved_ok", legacyConverter.print(legacy));
    }

    public void saveAndClose(Legacy legacy) {
        legacyRepository.save(legacy);
        messageUtil.infoDelayed("status_saved_ok", legacyConverter.print(legacy));
        forceClose();
    }

    public void delete(Legacy legacy) {
        String infoArg = legacyConverter.print(legacy);
        legacyRepository.delete(legacy);
        messageUtil.info("status_deleted_ok", infoArg);
    }
}