package com.jaxio.web.selenium.page.document;

import static org.openqa.selenium.By.cssSelector;

import org.openqa.selenium.WebElement;
import org.openqa.selenium.support.FindBy;

import com.jaxio.web.selenium.support.Page;
import com.jaxio.web.selenium.page.AbstractSearchPage;

@Page
public class DocumentSearchPage extends AbstractSearchPage {
    // search box
    @FindBy(id = "form:account")
    public WebElement account;

    public void clickEditDocument(String document) {
        client.click(cssSelector("button[title=\"Edit " + document + "\"]"));
    }

    public void clickAddDocument(String document) {
        client.click(cssSelector("button[title=\"Add " + document + "\"]"));
    }

    public void clickDeleteDocument(String document) {
        client.click(cssSelector("button[title=\"Delete " + document + "\"]"));
    }

    public void clickSelectDocument(String document) {
        client.click(cssSelector("button[title=\"Select " + document + "\"]"));
    }
}